import React from 'react';
import Link from "next/link";
import { getNtc } from "../modules/notice"

const NoticeList = (props, {getNtc}) => {

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getNtc());
    }, [])


    return (
        <>
            <Link href='/noticeDetail'><li key={props.props.NTC_ID} onClick={getNtc}>{props.props.NTC_TITLE}</li></Link>
        </>

    )
};


export default NoticeList;
