import React from 'react';
import axios from 'axios';
import Link from "next/link";

const NoticeList = () => {

    const {lists} = this.props;

    const Lists = lists.map(
        list => <Link href='/noticeDetail'><li key={list.NTC_ID} onClick={getId(list.NTC_ID)}>{list.NTC_TITLE}</li></Link>
    );
    return (
        <>
            <li>{Lists}</li>
        </>
    )
};
NoticeList.getInitialProps = async () => {
    const response = await axios.get('http://15.165.161.162:8086/api/web/notice/NoticeList?PAGE=1');
    return {
        lists : response.data.data[0].list
    }
};

export default NoticeList;
