import NoticeList from "../components/NoticeList";
import React, { useCallback} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { getNtc } from '../modules/notice'

const NoticeContainers = () => {
    const data = useSelector(state => state.notice.data);
    const dispatch = useDispatch();
    const OnChage = useCallback(() => dispatch(getNtc()), [dispatch]);
    return <NoticeList data={data} onChage={OnChage} />
};
NoticeContainers.getInitialProps = async () => {
    const response = await axios.get('http://15.165.161.162:8086/api/web/notice/NoticeList?PAGE=1');
    return {
        lists : response.data.data[0].list
    }
};

export default NoticeContainers;
