import React, {useState} from 'react';
import Link from "next/link";
import axios from 'axios';

const NoticeList = () => {

    const [lists, setList] = useState([]);
    const Lists = lists.map(
        list => <Link href='/noticeDetail'><li key={list.NTC_ID} onClick={onChage(list.NTC_ID)}>{list.NTC_TITLE}</li></Link>
    );
    return (
        <>
            {Lists}
        </>
    )
};
NoticeList.getInitialProps = async () => {
    const response = await axios.get('http://15.165.161.162:8086/api/web/notice/NoticeList?PAGE=1');
    return {
        setList : response.data.data[0].list
    }
};

export default NoticeList;
