import NoticeList from "../components/NoticeList";
import React from 'react';
import axios from 'axios';

const NoticeContainers = ({lists}) => {


    const Lists = lists.map(
        list => <NoticeList props={list} key={list.NTC_ID}/>
    );
    return (
        <>
            <li>{Lists}</li>
        </>
    )
};
NoticeContainers.getInitialProps = async () => {
    const response = await axios.get('http://15.165.161.162:8086/api/web/notice/NoticeList?PAGE=1');
    return {
        lists : response.data.data[0].list
    }
};

export default Index;
